import {useState} from "react"

export default function Input({personnes, setPersonnes}){

    // state ou données

    const [newpersonnes, setNempersonnes] = useState("")

    // comportements
    const handleSubmit = (e)=>{
        e.preventDefault()

        const copy = [...personnes]
        const id = new Date().getTime()
        const nom = newpersonnes
        copy.push({id, nom})
        setPersonnes(copy)

    }

    const handleChange = (e)=>{
        setNempersonnes(e.target.value)
    }

    // affichage

    return(
        <form onSubmit={handleSubmit}>
            <input onChange={handleChange} value={newpersonnes}/>
            <button>ajouter</button>
        </form>
    )
}
