import "./App.css"
import Part from "./Composants/Part"
import Menu from "./Composants/Menu"
import Header from "./Composants/Header"
import Donnees from "./Composants/Donnees"
import Footer from "./Composants/Footer"

import axios from "axios"
import {useState, useEffect, useContext} from "react"




export default function App(){

    const [data, setData] = useState([])

    const [searchQuery, setSearchQuery] = useState("");

    //pagination

    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [pageSize] = useState(15);


    const { selectedCategory, setSelectedCategory } = useContext(CategoryContext);


    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/produits/')
            setData(result.data)
            setTotalPages(Math.ceil(result.data.length / pageSize));
        }

        fetchdata()
    }, [pageSize])

    const handlePageChange = (pageNumber) => {
      setCurrentPage(pageNumber);
    };

    const filteredData = data
    .filter((item) => !selectedCategory || item.categorie.id === selectedCategory.id)
    .filter((item) => item.name.toLowerCase().includes(searchQuery.toLowerCase()));

    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    const currentData = filteredData.slice(startIndex, endIndex);


    return (
        <div className="App">
            <div className="Anime">
                <Part />
            </div>
            <nav>
                <Menu />
            </nav>
            <header>
                <Header />
            </header>
            <section>
                <Donnees 
                    currentData={currentData}
                />
            </section>
            <footer>
                <Footer />
            </footer>
        </div>
    )
}
