import "./App.css"
import Part from "./Composants/Part"
import Menu from "./Composants/Menu"
import Header from "./Composants/Header"
import Donnees from "./Composants/Donnees"
import Footer from "./Composants/Footer"

import axios from "axios"
import {useState, useEffect, useContext} from "react"

import {CategoryContext} from "./Composants/CategoryContext"


export default function App(){

    const [data, setData] = useState([])

    const { selectedCategory } = useContext(CategoryContext);

    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/produits/')
            setData(result.data)
        }

        fetchdata()
    })




    const [categories, setCategories] = useState([])

    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/categories/')
            setCategories(result.data)
        }

        fetchdata()
    }, [])




    const filteredData = data
    .filter((item) => !selectedCategory || item.categorie.id === selectedCategory.id)

    return (
        <div className="App">
            <div className="Anime">
                <Part />
            </div>
            <nav>
                <Menu categories={categories}/>
            </nav>
            <header>
                <Header />
            </header>
            <section>
                <Donnees filteredData={filteredData}/>
            </section>
            <footer>
                <Footer />
            </footer>
        </div>
    )
}
