import React, { useState, useEffect } from "react";
import { Page, Document, StyleSheet, PDFViewer, Text, View, Image } from '@react-pdf/renderer'; 
import "./PdfDocument.css"

const styles = StyleSheet.create({
  page: {
    flexDirection: 'column',
    backgroundColor: '#FFFFFF',
    padding: 20,
  },
  title: {
    fontSize: 28,
    marginBottom: 20,
    textAlign: 'center',
    fontWeight: 'bold',
    textTransform: 'uppercase',
  },
  itemContainer: {
    flexDirection: 'row', 
    alignItems: 'center',
    marginBottom: 15,
    paddingBottom: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#AAAAAA',
  },
  itemName: {
    fontSize: 23,
    fontWeight: 'bold',
  },
  itemPrice: {
    fontSize: 23,
    color: '#555555',
  },
  totalPrice: {
    fontSize: 23,
    fontWeight: 'bold',
    marginTop: 20,
    textAlign: 'right',
  },

  imageContainer: {
    alignItems: 'center', // Center the image horizontally
    marginTop: 20,
  },

  image: {
    width: 200, // Ajustez la largeur selon vos besoins
    height: 200, // Ajustez la hauteur selon vos besoins
  },

});






const PdfDocument = () => {


  // Utilise useState pour stocker les items du localStorage
  const [savedItems, setSavedItems] = useState([]);

  // Utilise useEffect pour récupérer les items depuis le localStorage au chargement du composant
  useEffect(() => {
    const savedData = localStorage.getItem("cartItems");
    if (savedData) {
      setSavedItems(JSON.parse(savedData));
    }
  }, []);

    const totalAmount = savedItems.reduce((total, item) => total + item.price, 0);



return (
    <Document>
      <Page style={styles.page}>
        <View>
          <Text style={styles.title} className="pdftitle">Ma Commande</Text>
          {savedItems.map((item) => (
            <View style={styles.itemContainer} key={item.id}>
            <View style={styles.imageContainer}> {/* Add a container for the image */}
              <Image src={item.image} style={styles.image} />
            </View>
            <View style={styles.textContainer}>
              <Text style={styles.itemName}c className="pdfnom">Article commandé: {item.name}</Text>
              <Text style={styles.itemPrice}>Tarif: {item.price} FCFA</Text>
            </View>
            </View>
          ))}
          <Text style={styles.totalPrice}>Total Price: {totalAmount} 
          FCFA</Text>
        </View>
      </Page>
    </Document>
  );
};

export default PdfDocument;
