import "./Menu.css"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from '@fortawesome/free-solid-svg-icons'
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons'
import { faTimes } from '@fortawesome/free-solid-svg-icons'

import {useState, useEffect, useContext} from 'react'
import axios from "axios"
import {CategoryContext} from "./CategoryContext"


export default function Menu(){


    const [categories, setCategories] = useState([])

    const { selectedCategory, setSelectedCategory } = useContext(CategoryContext);

    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/categories/')
            setCategories(result.data)
        }

        fetchdata()
    }, [])

    const handleCategorySelection = (nom)=>{
        setSelectedCategory(nom)

    }

    return(
        <>
        <div className="bigmenu">
            <div className="Menu">
                <label for="menu">
                    <FontAwesomeIcon icon={faBars} />
                </label>
                <input type="checkbox" id="menu" />
                <div className="name">YATTE</div>
                <input type="checkbox" id="panier" />
                <label for="panier">
                    <FontAwesomeIcon icon={faShoppingCart} /><span className="mnumn">
                    0
                </span>
                </label>

                <div className="leftmenu">
                    <p>Toutes les catégories</p>
                   {categories.map(categorie=>{
                       return(
                           <p>{categorie.nom}</p>
                        )
                   })}
                </div>
                <div className="cart">
                    <label for="panier">
                        <FontAwesomeIcon icon={faTimes} />
                    </label>
                </div>
            </div>
            <div className="barre">
            </div>
        </div>
        <div className="grdEcran">
            <p onClick={() => handleCategorySelection(null)}>Nos catégories</p>
                   {categories.map(categorie=>{
                       return(
                           <p>{categorie.nom}</p>
                        )
                   })}
        </div>
        </>
    )
}
