import {useParams} from "react-router-dom"
import axios from "axios"
import {useState, useEffect} from "react"
import "./Detail.css"


export default function Detail (){

    const [data, setData] = useState([])

    const {produitId}=useParams()


    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/produits/')
            setData(result.data)
        }

        fetchdata()
    }, [])


    const filteredData = data.filter(item => item.id === parseInt(produitId))
    const item = filteredData[0]

    return(
        <>
            {item && (
                <div className="detailCont">
                    <div className="detailImg"><img src={item.image} alt={item.name} /></div>
                    <div className="detailNom">{item.name}</div>
                    <div className="detailPrix">{item.price}</div>
                    <div className="detailDesc">{item.description}</div>
                </div>
            )}
        </>
    )
}
