import {useParams} from "react-router-dom"
import axios from "axios"
import {useState, useEffect} from "react"
import "./Detail.css"


function formatAmountWithSeparators(amount) {
  return amount.toFixed(0).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
}



export default function Detail (){

    const [data, setData] = useState([])

    const {produitId}=useParams()


    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/produits/')
            setData(result.data)
        }

        fetchdata()
    }, [])


    const filteredData = data.filter(item => item.id === parseInt(produitId))
    const item = filteredData[0]

    return(
        <>
            {item && (
                <div className="detailCont">
                    <div className="detailImg"><img src={item.image} alt={item.name} /></div>
                    <div className="detailNom">{item.name}</div>
                    <div className="detailPrix">{formatAmountWithSeparators(item.price)} FCFA</div>
                    <div className="detailDesc">
                    <h3>Description : {item.nom}</h3>
                    <p>{item.description}</p>
                    </div>
                </div>
            )}
        </>
    )
}
