import React, { useState, useEffect } from "react";
import { Page, Document, StyleSheet, PDFViewer, Text, View } from '@react-pdf/renderer'; 



const styles = StyleSheet.create({
  page: {
    flexDirection: 'column',
    backgroundColor: '#E4E4E4',
    padding: 10,
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
  },
  content: {
    fontSize: 12,
  },
});





const PdfDocument = () => {


  // Utilise useState pour stocker les items du localStorage
  const [savedItems, setSavedItems] = useState([]);

  // Utilise useEffect pour récupérer les items depuis le localStorage au chargement du composant
  useEffect(() => {
    const savedData = localStorage.getItem("cartItems");
    if (savedData) {
      setSavedItems(JSON.parse(savedData));
    }
  }, []);

  alert(savedItems)



  return (
    <Document>
      <Page style={styles.page}>
        <View>
          <Text style={styles.title}>Mon Reçu</Text>
          <Text style={styles.content}>
            {savedItems.map((item) => {
                return(
                    <>
                        <p>{item.nom}</p>
                    </>
                )
              })}
          </Text>
        </View>
      </Page>
    </Document>
  );
};

export default PdfDocument;
