import {useState} from "react"

export default function Input({animaux, setAnimaux}){

    //state ou données

    const[newAnimo, setNewAnimo] = useState("")


    // comportements

    const handleSubmit = (e)=>{
        e.preventDefault()

        const id = new Date().getTime()
        const nom = newAnimo

        const copy = [...animaux]
        copy.push({id,nom})
        setAnimaux(copy)
        setNewAnimo("")
    }

    const handleChange = (e)=>{
        setNewAnimo(e.target.value)
    }

    //affichage

    return(
        <>
            <form onSubmit={handleSubmit}>
                <input value={newAnimo} onChange={handleChange}/>
                <button>ajouter</button>
            </form>
        </>
    )
}
