export default function Norah (){

    // ETATS

    const [compteur, setCompteur] = useState(0)

    // COMPORTEMENTS

    const handleClick = ()=>{
        setCompteur(compteur + 1)
    }

    const handleDelete = ()=>{
        setCompteur(compteur-1)
    }

    // AFFICHAGE
    return(
        <>
            <h1>compteur : {compteur}</h1>
            <button onClick={handleClick}>incrementer</button>
            <button>clear</button>
            <button onClick={handleDelete}>decrementer</button>
        </>
    )
}
