import Produit from "./Produit"
import "./Donnees.css"
import SearchBar from "./SearchBar"
import axios from "axios"
import {useState, useEffect, useContext} from "react"
import {CategoryContext} from "./CategoryContext"


export default function Donnees(){

    const [data, setData] = useState([])

    const [searchQuery, setSearchQuery] = useState("");

    //pagination

    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [pageSize] = useState(15);


    const { selectedCategory } = useContext(CategoryContext);


    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/produits/')
            setData(result.data)
            setTotalPages(Math.ceil(result.data.length / pageSize));
        }

        fetchdata()
    }, [pageSize])


    const handlePageChange = (pageNumber) => {
      setCurrentPage(pageNumber);
    };

    const filteredData = data
    .filter((item) => !selectedCategory || item.categorie.id === selectedCategory.id)
    .filter((item) => item.name.toLowerCase().includes(searchQuery.toLowerCase()));

      const startIndex = (currentPage - 1) * pageSize;
      const endIndex = startIndex + pageSize;
      const currentData = filteredData.slice(startIndex, endIndex);


    return (
        <>
            <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery}/>
            <div className="Donnees">
            {currentData.map(item=>{
                return <Produit keys={item.id} {...item} item={item} />
            })}
            </div>
        </>
    )
}
