import {useState} from "react"
import Personnes from "./Personnes"
import Input from "./Input"

export default function Donnees(){

    // state ou données

    const [personnes, setPersonnes] = useState([
        {id:1, nom:"Davy", age:39},
        {id:2, nom:"Norah", age:1},
        {id:3, nom:"Mika", age:30},
        {id:4, nom:"Emma", age:17}
    ])


    // comportements

    const handleClick = (id)=>{
        const copyPersonnes = [...personnes]
        const filtrePerso = copyPersonnes.filter(item =>item.id!==id)
        setPersonnes(filtrePerso)
    }



    // affichage

    return(
        <>
            <ul>
                {personnes.map(personne=>{
                    return <Personnes keys={personne.id} {...personne} personne={personne} handleClick={handleClick}/>
                })}
            </ul>

            <Input Personnes={Personnes} setPersonnes={setPersonnes}/>
        </>
    )
}
