import {useState} from "react"

export default function Norah (){

    // ETATS

    const [person, setPerson] = useState([
        {id:1, nom:"Emma"},
        {id:2, nom:"Davy"},
        {id:3, nom:"Norah"}
    ])

    // COMPORTEMENTS

    const handleDelete = (id)=>{
        const newperson = [...person]
        const newpersonFind = newperson.find(pa=>pa.id==id)
        setPerson(newpersonFind)
    }



    // AFFICHAGE
    return(
        <>
            <h3>liste de personnes</h3>
            <ul>
                {person.map(pers=>{
                    return (
                        <li keys={pers.id}>{pers.nom}
                            <button onClick={()=>handleDelete(pers.id)}>
                                X
                            </button>
                        </li>
                    )
                })}
            </ul>
        </>
    )
}
