const PdfDocument = ({ name, items }) => {
  return (
    <Document>
      <Page style={styles.page}>
        <View>
          <Text style={styles.title}>{name}'s Reçu</Text>
          {items.map((item, index) => (
            <Text key={index} style={styles.content}>
              {item.name}: {item.quantity} x {item.price} FCFA
            </Text>
          ))}
        </View>
      </Page>
    </Document>
  );
};
