import { ReactComponent as IconCreditCard2Front } from "bootstrap-icons/icons/credit-card-2-front.svg";
import { ReactComponent as IconCart3 } from "bootstrap-icons/icons/cart3.svg";


import {useState, useEffect} from "react"

import axios from "axios"

import {useCart} from "react-use-cart"

import { toast } from 'react-toastify';

import {Link} from "react-router-dom"

import { useNavigate } from "react-router-dom";


import { useOrderContext } from './OrderContext';

import PdfDocument from "./PdfDocument"

import './Order.css'



export default function Order({closeModal}){

        //state (état ou données)

    const [nom, setNom] = useState("")
    const [phone, setPhone] = useState(null)


    const {items,cartTotal, emptyCart} = useCart()


    const navigate = useNavigate();



    const { setOrderSuccess } = useOrderContext();

    //sauvegarde des items 
    useEffect(() => {
        localStorage.setItem("cartItems", JSON.stringify(items));
    }, [items]);


    //comportements

    const handleSubmit = (e)=>{
        e.preventDefault()


        for(let i=0 ; i<items.length; i++){

            axios({
              method: 'post',
              url: 'http://127.0.0.1:8000/api/cd/orders/',
              data: {
                "nom": nom,

                "phone": phone,

                "quantity": JSON.stringify(items[i].quantity),

                "produit": JSON.stringify(items[i].id)
              },
            })

            .then(response => {
              // La commande a été passée avec succès, vider le panier
              emptyCart();


            })

            .catch(error => {
              console.log(error);
            });
        }

        toast.success("Commande prise en compte. Livraison dans un delai de 24h. Merci!",  { autoClose: 10000 }) ;

        setNom("")
        setPhone("")

        //alert("commande passé avec succès")

        closeModal();

        //navigate('/')

        setTimeout(() => {
            window.location.reload();
        }, 10000);

        setOrderSuccess(true);


        localStorage.setItem('orderSuccess', 'true');

    }



    const handleChange = (e)=>{
        setNom(e.target.value)
    }

    const handleOnChange = (e)=>{
        setPhone(e.target.value)
    }

    return(
        <>
        <div className="Commmando">

            <div className="colOrder">
              <div className="cardOrder">
                <div className="cardheader">
                  <IconCreditCard2Front className="IconCreditCard2Front" /> Formulaire De Commande
                </div>
                <form onSubmit={handleSubmit}>
                <div className="cardOrderbody">

                  <div className="row g-3">
                    <div className="col-md-6">
                      <input
                        value={nom}
                        type="text"
                        className="inputOrder"
                        placeholder="Nom"
                        onChange={handleChange}
                       required="true"/>
                    </div>
                    <div className="col-md-6">
                      <input
                        value={phone}
                        type="number"
                        className="inputOrder"
                        placeholder="Numéro de Téléphone"
                        onChange={handleOnChange}
                       required="true"/>
                    </div>
                  </div>

                </div>
                <div className="cardOrderfooter">

                  <button type="submit" className="btn btn-primary">
                    valider
                  </button>
                </div>
                </form>
              </div>
            </div>
            <div className="col-md-12">
              <div className="card">
                <div className="card-header">
                  <IconCart3 className="i-va" />
                  <span className="ms-3">MA COMMANDE</span>
                </div>
                {items.map((item,index)=>{
                        return(
                <ul className="list-group list-group-flush" key={index}>
                  <li className="list-group-item d-flex justify-content-between lh-sm">
                    <div>
                      <h6 className="my-0">{item.name}</h6>
                    </div>
                    <span className="text-muted">{item.price*item.quantity} FCFA</span>
                  </li>
                </ul>)
                })}
                <div className="list-group-item d-flex justify-content-between py-3 mx-3">
                    <span>Total </span>
                    <strong>{cartTotal} FCFA</strong>
                </div>
              </div>

            </div>

            <button className="btn btn-danger mt-2 ms-3" style={{width:"90%"}} onClick={closeModal}>
                annuler la commande
            </button>
        </div>
        </>
    )
}
